<?php/** * Settings page for Stripe. * * @package    View * @subpackage Stripe */// Globals.global $psts, $current_site, $current_user;?><form action="<?php echo $psts->checkout_url( $bid, $domain ); ?>" method="post">	<input type="hidden" name="level" value="<?php echo $level; ?>"/>	<input type="hidden" name="period" value="<?php echo $period; ?>"/>	<input type="hidden" name="tax-type" value="none"/>	<input type="hidden" name="tax-country" value="none"/>	<input type="hidden" name="tax-evidence" value=""/>	<?php if ( $new_blog ) : ?>		<input type="hidden" name="new_blog" value="1"/>	<?php endif; ?>	<?php if ( $bid ) : ?>		<input type="hidden" name="bid" value="<?php echo $bid; ?>"/>	<?php endif; ?>	<?php if ( $activation_key ) : ?>		<input type="hidden" name="activation" value="<?php echo $activation_key; ?>"/>		<?php if ( $user_name ) : ?>			<input type="hidden" name="blog_username" value="<?php echo $user_name; ?>"/>			<input type="hidden" name="blog_email" value="<?php echo $user_email; ?>"/>			<input type="hidden" name="blog_name" value="<?php echo $blogname; ?>"/>			<input type="hidden" name="blog_title" value="<?php echo $blog_title; ?>"/>		<?php endif; ?>	<?php endif; ?>	<script src="https://checkout.stripe.com/checkout.js" class="stripe-button"	        data-key="<?php echo $public_key; ?>"	        data-name="<?php echo $current_site->site_name; ?>"	        data-description="Access for a year"	        data-image="<?php echo get_site_icon_url( 512, '', 1 ); ?>"	        data-amount="<?php echo $amount; ?>"	        data-locale="<?php echo get_locale(); ?>"	        data-email="<?php echo $current_user->user_email; ?>"	        data-label="<?php esc_html_e( 'Subscribe', 'psts' ); ?>">	</script></form>